                WriteLiteral(@"

    <script>
        $(function () {
            function loadHeaderCart() {

            }
            $("".removecart"").click(function () {
                var productid = $(this).attr(""data-mahh"");
                var sizeid = $(this).attr(""data-makc"");
                $.ajax({
                    url: ""api/cart/remove"",
                    type: ""POST"",
                    dataType: ""JSON"",
                    data: {
                        productID: productid,
                        sizeID: sizeid
                    },
                    success: function (result) {
                        if (result.success) {
                            loadHeaderCart();//Reload lai gio hang
                            location.reload();
                        }

                    },
                    error: function (rs) {
                        alert(""Remove Cart Error !"")
                    }
                });
            });
            $("".cartItem"").click(function () {");
                WriteLiteral(@"
                var productid = $(this).attr(""data-mahh"");
                var sizeid = $(this).attr(""data-makc"");
                var soluong = parseInt($(this).val());
                $.ajax({
                    url: ""api/cart/update"",
                    type: ""POST"",
                    dataType: ""JSON"",
                    data: {
                        productID: productid,
                        sizeID: sizeid,
                        amount: soluong
                    },
                    success: function (result) {
                        if (result.success) {
                            loadHeaderCart();//Reload lai gio hang
                            window.location = 'cart.html';
                        }
                    },
                    error: function (rs) {
                        alert(""Cập nhật Cart Error !"")
                    }
                });
            });
        });
    </script>
");
